mapper.select:
SELECT MEMBER_IDX, MEMBER_NAME, MEMBER_EMAIL, MEMBER_PHONE, MEMBER_BIRTHDATE, MEMBER_GRADE, MEMBER_NICKNAME
FROM MEMBER
WHERE MEMBER_EMAIL = #{email} AND MEMBER_PASSWORD = #{password}

mapper.update:
UPDATE MEMBER
MEMBER_NAME = #{name}, MEMBER_NICKNAME = #{nickname}, MEMBER_PHONE = #{phone}, MEMBER_PASSWORD = #{password}, MEMBER_TEMP_PWD = #{tempPassword}, MEMBER_NATIONAL = #{national}, MEMBER_MOD_DATE = SYSDATE
WHERE MEMBER_IDX = #{no}

mapper.insert:
INSERT INTO MEMBER (MEMBER_IDX, MEMBER_NAME, MEMBER_PHONE
	, MEMBER_NICKNAME, MEMBER_BIRTHDATE, MEMBER_EMAIL, MEMBER_NATIONAL
	, MEMBER_PASSWORD, MEMBER_CRE_DATE, MEMBER_MOD_DATE, MEMBER_GRADE)
VALUES (SEQ_MEMBER_NO.NEXTVAL, #{name}, #{phone}
	, #{nickname}, #{birthdate}, #{email}, #{national}
	, #{password}, SYSDATE, SYSDATE, #{grade})

mapper.select:
SELECT MEMBER_EMAIL
FROM MEMBER
WHERE MEMBER_NAME = #{name}
AND MEMBER_PHONE = #{phone}
AND MEMBER_BIRTHDATE = #{birthdate}

SELECT MEMBER_PASSWORD, MEMBER_NAME
FROM MEMBER
WHERE MEMBER_NAME = #{name}
AND MEMBER_EMAIL = #{email}

SELECT count(*)
FROM MEMBER
WHERE MEMBER_NICKNAME = #{nickname}

SELECT MEMBER_IDX, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_EMAIL, MEMBER_PHONE, MEMBER_PASSWORD, MEMBER_NATIONAL
FROM MEMBER
WHERE MEMBER_IDX = #{no}
